__int128 read() {
    __int128 x = 0, f = 1;
    char ch = getchar();
    while (ch < '0' || ch > '9') {
        if (ch == '-') f = -1;
        ch = getchar();
    }
    while (ch >= '0' && ch <= '9') {
        x = x * 10 + ch - '0';
        ch = getchar();
    }
    return x * f;
}

void print(__int128 x) {
    if (x < 0) {
        putchar('-');
        x = -x;
    }
    if (x > 9) print(x / 10);
    putchar(x % 10 + '0');
}

void segmentedSieve(long long l, long long r) {
    if (l % 2 == 0) {
        l++;
    }
    vector<char> is_prime_seg(r - l + 1, true);
    for (long long i : primes) {
        if (i == 2 || i == 0) {
            continue;
        }
        if (i * i > r) {
            break;
        }
        for (long long j = max(i * i, (l + i - 1) / i * i); j <= r; j += i) {
            is_prime_seg[j - l] = false;
        }
    }

    for (int i = 0; i < r - l + 1; i += 2) {
        if (is_prime_seg[i]) {
            if (total + l + i > sum) {
                return;
            }
            total += l + i;
            p = l + i;
        }
    }
    return;
}

auto udiv = [](const uint64_t& a, const uint32_t& b) -> uint64_t {
    return (double)a / b + 1e-9;
};

__int128 phi(__int128 m, int n){
    if (n == 0) {
        return m * (m + 1) / 2;
    }
    if (m < M && n < N) {
        return dp[n][m];
    }
    if ((long long)primes[n] * primes[n] >= m && m < P) {
        return prime_sum[m] - prime_sum[primes[n]] + 1;
    }
    return phi(m, n - 1) - phi(udiv(m, primes[n]), n - 1) * primes[n];
}

__int128 lehmer(__int128 m){
    if (m < P) {
        return prime_sum[m];
    }
    auto it = mp.find(m);
    if (it != mp.end()) {
        return it->second;
    }
    int s = sqrt(0.5 + m), y = cbrt(0.5 + m);
    long long a = prime_cnt[y], b = prime_sum[y];

    __int128 res = phi(m, a) + b - 1;
    for (int i = a + 1; i <= prime_cnt[s]; i++) {
        res = res - (lehmer(udiv(m, primes[i])) - prime_sum[primes[i] - 1]) * primes[i];
    }
    return mp[m] = res;
}

void sieve(){
    is_prime[2] = true;
    for (int i = 3; i < P; i += 2) {
        is_prime[i] = true;
    }
    for (int i = 3; i * i < P; i += 2){
        for (int j = i * i; is_prime[i] && j < P; j += (i << 1)){
            is_prime[j] = false;
        }
    }
    for (int i = 1; i < P; i++){
        prime_cnt[i] = prime_cnt[i - 1] + is_prime[i];
        prime_sum[i] = prime_sum[i - 1] + (is_prime[i] ? i : 0);
        if (is_prime[i]) {
            primes.push_back(i);
        }
    }
}

__int128 conv(string s) {
    __int128 sum = 0;
    for (int i = 0; i < s.size(); i++) {
        sum += (__int128)(s[i] - '0');
        if (i != s.size() - 1) {
            sum *= (__int128)10;
        }
    }
    return sum;
}


int main() {
    //IOS;
    sieve();
    for (int m = 0; m < M; m++) {
            dp[0][m] = (long long)m * (m + 1) / 2;
        }
    for (int n = 1; n < N; n++){
        for (int m = 1; m < M; m++){
            dp[n][m] = dp[n - 1][m] - dp[n - 1][udiv(m, primes[n])] * primes[n];
        }
    }
    sum = read();
    if (sum <= conv(limits[0])) {
        long long l = 1, r = 1e11, ans = 1e11;
        while (l <= r) {
            long long mid = (l + r) / 2;
            __int128 ct = lehmer(mid);
            if (ct < sum) {
                l = mid + 1;
            } else {
                if (ct == sum) {
                    ans = min(ans, mid);
                }
                r = mid - 1;
            }
        }
        cout << ans;
        return 0;
    }
    long long delta = 1e9;
    /*for (long long d = 1e11; d < 1e12; d += delta) {
        __int128 ct = lehmer(d);
        putchar('"');print(ct);putchar('"');putchar(',');
    }
    return 0;*/
    long long L = 1, R = 1e12; int ind = 0;
    for (long long d = 1e11, j = 0; d < 1e12; d += delta, j++) {
        __int128 lim = conv(limits[j]);
        if (sum > lim) {
            L = d;
            ind = j;
        }
    }
    for (long long d = 1e12 - delta, j = limits.size() - 1; d >= 1e11; d -= delta, j--) {
        __int128 lim = conv(limits[j]);
        if (sum < lim) {
            R = d;
        }
    }
    total = conv(limits[ind]);
    int i = 1;
    while (L <= R) {
        if (i > 5) {
            break;
        }
        long long mid = (L + R) / 2;
        __int128 ct = lehmer(mid);
        if (ct < sum) {
            L = mid + 1;
            total = ct;
        } else {
            R = mid - 1;
        }
        i++;
    }
    segmentedSieve(L, R);
    cout << p;
    //201467077743744681014 1e11
    //18435588552550705911377 1e12

    return 0;
}
